var variables = runtimeScene.getVariables();
var dest_x = variables.get("dest_x").getValue();
var dest_y = variables.get("dest_y").getValue();

var is_colliding = !!objects.find((f) => f.x == dest_x && f.y == dest_y);

if (!is_colliding) {
    variables.get("moving").setNumber(1);
    variables.get("turn").add(1);
}

const player = runtimeScene.getObjects("Player")[0];
const fires = runtimeScene.getObjects("Fire");

const variables = runtimeScene.getVariables();
const grid_size = variables.get("grid_size").getValue();

// possible positions to put the fire
let positions = [];

for (let i = -1; i <= 1; i++) {
    for (let j = -1; j <= 1; j++) {
        if (i != 0 || j != 0) positions.push([i, j]);
    }
}

fires.forEach((f) => {
    const distance_x = (f.x - player.x) / grid_size;
    const distance_y = (f.y - player.y) / grid_size;

    positions = positions.filter((p) => {
        return p[0] != distance_x || p[1] != distance_y;
    });
});

const newFire = runtimeScene.createObject("Fire");
const position = positions[Math.floor(Math.random()*positions.length)];
newFire.x = player.x + position[0] * grid_size;
newFire.y = player.y + position[1] * grid_size;
